		'article_article' => '<br /><font color=\\"#000000\\"><strong><font size=\\"2\\"><font color=\\"#000033\\"><a href=\\"/readarticle.php?article_id=160\\"><strong><font size=\\"2\\">Aktualności</font></strong></a></font> <br /><br /><strong><a href=\\"/readarticle.php?article_id=157\\" target=\\"_blank\\"><strong>Dokumenty</strong></a></strong> <br /><br /><strong><a href=\\"/readarticle.php?article_id=159\\"><strong>Skład Rady</strong></a></strong> <br /><br /><br /><br /></font></strong></font>',
